import {jsPDF} from 'jspdf'

let img = 'data:image/jpg;base64,'

function generate(text='',filename='',PATH=''){
    let pdf_gen = new jsPDF({'orientation':'landscape','unit':'px','format':'letter'})
    
    pdf_gen.setFontSize(30)
    pdf_gen.setTextColor('#345')
    pdf_gen.text(text,10,100)
    pdf_gen.addImage(img,'JPEG',10,20,30,30)
    pdf_gen.text('forms',50,45)
    pdf_gen.save(PATH+`/pdfs/${filename}.pdf`)
}

export default generate